import React, { Component } from "react";
import axios from 'axios';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    Redirect
} from "react-router-dom";

class Welcome extends Component {
    render() {
        return (
            <div ><h1>Welcome to JISS</h1></div>
        );
    }
}
export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = { username: '', password: '', login: false };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.Auth = this.Auth.bind(this);

    }
    Auth() {
        console.log('HI');
        axios.post("/api/isLoggedIn", null)
            .then(res => {
                if(res.data.login_status=='1')
                {
                    console.log('true');
                    this.setState({login : true});
                    console.log(this.state.login);
                    console.log('true');
                }
                else
                {
                    console.log('false');
                    this.setState({login:false});
                }

            });

    }
    componentDidMount(){
        this.Auth();
        console.log(this.state.login);
    }
    handleChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }
    handleSubmit(event) {
        //console.log('Submitting'); //For Debug
        alert('A username was submitted: ' + this.state.username);
        const requestOptions = {
            'username': this.state.username, 'password': this.state.password
        };

        axios.post('/api/login', requestOptions)
            .then(res => {
                console.log(res.data);
                this.setState({ login: res.data.login_status ? true : false });
                console.log(this.state.login);
            });
        if (this.state.login) {
            return <Redirect to="/home" />
        }

    }
    render() {
        if (this.login) {
            return (<Redirect to="/home" />)
        }
        else {
            return (
                <div>
                    <Welcome />
                    <form onSubmit={this.handleSubmit}>
                        <h3>Please Sign In</h3>

                        <div className="form-group">
                            <label>Username</label>
                            <input type="text" name="username" onChange={this.handleChange} className="form-control" placeholder="Enter Username" />
                        </div>
                        <br />
                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" name="password" onChange={this.handleChange} className="form-control" placeholder="Enter password" />
                        </div>
                        {/*
                    <div className="form-group">
                        <div className="custom-control custom-checkbox">
                            <input type="checkbox" className="custom-control-input" id="customCheck1" />
                            <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                        </div>
                    </div>*/}

                        <button type="submit" className="btn btn-primary btn-block">Submit</button>
                        {/*<p className="forgot-password text-right">
                        Forgot <a href="#">password?</a>
                </p>*/}
                    </form>
                </div>
            );
        }

    }
}
