import React, { Component } from "react";
import axios from 'axios';

function Auth() {
    var LoggedIn = false;
    axios.post("/api/isLoggedIn",null)
        .then(res => {
            console.log('Login Status::');
            console.log(res.data.login_status);
            if(res.data.login_status)
            {
                this.LoggedIn = true;
            }
            else
            {
                this.LoggedIn = false;
            }            
        });
    return LoggedIn;
}
