import React, { Component } from "react";
import axios from 'axios';

function Auth() {
    var LoggedIn = false;
    axios.post("/api/isLoggedIn",null)
        .then(res => {
            LoggedIn = (res.data.login_status ? true : false);
        });
    return LoggedIn;
}
