import React, { Component } from "react";
import GridLayout from 'react-grid-layout';
import axios from 'axios';
import Dropdown from "react-dropdown";
import 'bootstrap/dist/css/bootstrap.min.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import './registrar.css';
import { BrowserRouter as Router } from "react-router-dom";
import LogoutButton from "./logoutbutton"
import './form.css';
export default class ViewFreeSlot extends Component {
    constructor(props) {
        super(props);
        this.state = {
            free_slot: null,
            query_date: new Date()
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    handleSubmit(e) {
        e.preventDefault();
        var errors = {
            usr_name_error: false,
            username_error: false,
            password_error: false,
            usr_type_error: false,
            usr_addr_error: false
        }
        const requestOptions = {
            'day': this.state.query_date.getDate().toString(),
            'month': this.state.query_date.getMonth().toString(),
            'year': this.state.query_date.getFullYear().toString()
        };
        console.log(requestOptions);
        axios.post('/api/queryFreeSlot', requestOptions)
            .then(res => {
                console.log(res.data);
                this.setState({ free_slot: res.data.free_slot });
            })
            .catch(err => {
                alert('Error in Server ' + err.response.status);
                console.log(err.response);
            });
    }
    handleChange(e) {

    }
    render() {
        if (this.state.free_slot != null) {

        }
        else {
            return (
                <Router>
                    <div className="Registrar">
                        <div className="Registrar-header">
                            <button
                                onClick={this.props.goback}
                                style={{ marginLeft: "auto" }}
                                className="btn btn-primary "
                            >
                                Go Back
                        </button>
                            <form onSubmit={this.handleSubmit}>
                                <h3>Query Free Slots</h3>
                                <div className="form-group">
                                    Enter Date: <DatePicker dateFormat="dd-MM-y" selected={this.state.query_date} onChange={date => this.setState({ query_date: date }, () => { console.log(this.state.query_date) })} />
                                </div>
                                <div className="form-group">
                                    <button type="submit" className="btn btn-primary btn-block">Submit</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </Router>
            );
        }
    }
}
